import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Form from "react-bootstrap/Form";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import "./form.css";

function TournamentForm() {
  const [inputs, setInputs] = useState({});
  const navigate = useNavigate();
  const tournamentInfo = localStorage.getItem("tournament-info");

  const handleChange = (event) => {
    const target = event.target;
    const name = target.name;
    const value = target.value;
    setInputs((values) => ({ ...values, [name]: value }));
  };

  const handleCreateTournament = (event) => {
    event.preventDefault();
    const requestOptions = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        name: inputs.name,
        type: inputs.type,
        participants: inputs.numberOfParticipants,
      }),
    };
    fetch("http://127.0.0.1:5000/tournament", requestOptions)
      .then((response) => {
        if (!response.ok) {
          throw new Error("API request was not successful!");
        } else if (response.ok) {
          navigate("/edit-tournament", {
            parameters: response.json(response),
          });
        }
        return true;
      })
      .catch((error) => {
        alert("Failed to create tournament");
        console.log("There has been a problem", error);
      });
  };

  const handleOpenTournament = (event) => {
    event.preventDefault();
    const requestOptions = {
      method: "GET",
      headers: { "Content-Type": "application/json" },
      params: { name: inputs.existingTournamentName },
    };
    fetch(
      "http://127.0.0.1:5000/tournament/" + inputs.existingTournamentName,
      requestOptions
    ).then((response) => {
      if (!response.ok) {
        alert("Tournament not found");
        throw new Error("API request was not successful!");
      } else if (response.ok) {
        navigate("/tournament", { parameters: response.json(response) });
      }
      return true;
    });
  };

  async function handleEditTournament(event) {
    event.preventDefault();
    const name = inputs.existingTournamentName;
    const result = await fetch("http://127.0.0.1:5000/tournament/" + name, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
    });
    result = await result.json();
    console.warn("result", result);
    localStorage.setItem("tournament-info", JSON.stringify(result));
    navigate(`/edit-tournament?params=${result}`);
  }

  useEffect(() => {
    if (tournamentInfo) {
      navigate("/edit-tournament");
    }
  }, []);

  // const handleEditTournament = (event) => {
  //   event.preventDefault();
  //   const requestOptions = {
  //     method: "GET",
  //     headers: { "Content-Type": "application/json" },
  //     params: { name: inputs.existingTournamentName },
  //   };
  //   fetch(
  //     "http://127.0.0.1:5000/tournament/" + inputs.existingTournamentName,
  //     requestOptions
  //   )
  //     .then((response) => {
  //       if (!response.ok) {
  //         alert("Tournament not found")
  //         throw new Error("API request was not successful!");
  //       } else if (response.ok) {
  //         navigate(`/edit-tournament$?params=${response.json(response)}`);
  //       }
  //       return true;
  //     })
  // };

  return (
    <Container>
      <Row className="align-items-center">
        <Col>
          <Form className="register-form">
            <Form.Group controlId="tournamentName">
              <Form.Label>TOURNAMENT NAME:</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter tournament name"
                name="tournamentName"
                onChange={handleInputChange}
              />
            </Form.Group>
            <Form.Group controlId="tournamentType">
              <Form.Label>TOURNAMENT TYPE:</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter tournament type"
                name="tournamentType"
                onChange={handleInputChange}
              />
            </Form.Group>
            <Form.Group controlId="numberOfParticipants">
              <Form.Label>NUMBER OF PARTICIPANTS:</Form.Label>
              <Form.Control
                type="text"
                placeholder="4"
                name="numberOfParticipants"
                onChange={handleInputChange}
              />
            </Form.Group>
            <Link
              className="btn btn-primary"
              to={{
                pathname: "",
                state,
              }}
            >
              CREATE TOURNAMENT
            </Link>
          </Form>
        </Col>
        <Col lg={1}></Col>
        <Col>
          <Form className="enter-form">
            <Form.Group controlId="existingTournamentName">
              <Form.Label>TOURNAMENT NAME:</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter tournament name"
                name="existingTournamentName"
                onChange={handleInputChange}
              />
            </Form.Group>
            <Link
              className="btn btn-primary"
              to={{
                pathname: "",
                state,
              }}
            >
              CREATE TOURNAMENT
            </Link>
            <Link
              className="btn btn-primary"
              to={{
                pathname: "",
                state,
              }}
            >
              CREATE TOURNAMENT
            </Link>
          </Form>
        </Col>
      </Row>
    </Container>
  );

  return (
    <Container>
      <Row className="align-items-center">
        <Col>
          <form id="tournament_form" onSubmit={handleCreateTournament}>
            <label>TOURNAMENT NAME:</label>
            <input
              className="input"
              id="inputField"
              type="text"
              name="name"
              value={inputs.name || ""}
              onChange={handleChange}
            />
            <label>TOURNAMENT TYPE:</label>
            <input
              className="input"
              id="inputField"
              type="text"
              name="type"
              value={inputs.type || ""}
              onChange={handleChange}
            />
            <label>NUMBER OF PARTICIPANTS:</label>
            <input
              className="input"
              id="inputField"
              type="text"
              name="numberOfParticipants"
              value={inputs.numberOfParticipants || ""}
              onChange={handleChange}
            />
            <input
              className="btn"
              id="inputButton"
              type="submit"
              value="CREATE TOURNAMENT"
            />
          </form>
        </Col>
        <Col lg={1}></Col>
        <Col>
          <form>
            <label>EXISTING TOURNAMENT NAME:</label>
            <input
              className="input"
              id="inputField"
              type="text"
              name="existingTournamentName"
              value={inputs.existingTournamentName || ""}
              onChange={handleChange}
            />
            <button
              className="btn"
              id="existingButton"
              onClick={handleOpenTournament}
            >
              OPEN TOURNAMENT
            </button>
            <button
              className="btn"
              id="existingButton"
              onClick={handleEditTournament}
            >
              EDIT TOURNAMENT
            </button>
          </form>
        </Col>
      </Row>
    </Container>
  );
}

export default TournamentForm;
