import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./form.css";

function TournamentForm() {
  const [inputs, setInputs] = useState({});
  const navigate = useNavigate();

  const handleChange = (event) => {
    const target = event.target;
    const name = target.name;
    const value = target.value;
    setInputs((values) => ({ ...values, [name]: value }));
  };

  const handleSubmit = (event) => {
    const requestOptions = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        tournament_name: inputs.name,
        tournament_type: inputs.type,
        participants: inputs.numberOfParticipants,
      }),
    };
    fetch("http://127.0.0.1:5000/tournament", requestOptions).then((res) =>
      res.json().then(navigate("/about"))
    );
    event.preventDefault();
  };

  return (
    <div className="container-fluid">
      <form id="tournament_form" onSubmit={handleSubmit}>
        <label>
          TOURNAMENT NAME:
          <input
            className="input"
            id="inputField"
            type="text"
            name="name"
            value={inputs.name || ""}
            onChange={handleChange}
          />
        </label>
        <label>
          TOURNAMENT TYPE:
          <input
            className="input"
            id="inputField"
            type="text"
            name="type"
            value={inputs.type || ""}
            onChange={handleChange}
          />
        </label>
        <label>
          NUMBER OF PARTICIPANTS:
          <input
            className="input"
            id="inputField"
            type="text"
            name="numberOfParticipants"
            value={inputs.numberOfParticipants || ""}
            onChange={handleChange}
          />
        </label>
        <input
          className="btn btn-primary"
          id="inputButton"
          type="submit"
          value="GENERATE"
        />
      </form>
    </div>
  );
}

export default TournamentForm;
