import React, { useState } from "react";
import { useNavigate} from "react-router-dom";
import "./form.css";

function TournamentForm() {
  const [inputs, setInputs] = useState({});

  const handleChange = (event) => {
    const target = event.target;
    const name = target.name;
    const value = target.value;
    setInputs((values) => ({ ...values, [name]: value }));
  };

  const handleSubmit = (event) => {
    const requestOptions = {
      method: "POST",
      redirect: "/about",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        tournament_name: inputs.name,
        tournament_type: inputs.type,
        participants: inputs.numberOfParticipants,
      }),
    };
    fetch("http://127.0.0.1:5000/tournament", requestOptions).then((res) =>
    res.json()
    );
    event.preventDefault();
  };

  return (
    <div className="center">
      <form onSubmit={handleSubmit}>
        <label>
          TOURNAMENT NAME:
          <input
            className="input"
            type="text"
            name="name"
            value={inputs.name || ""}
            onChange={handleChange}
          />
        </label>
        <label>
          TOURNAMENT TYPE:
          <input
            className="input"
            type="text"
            name="type"
            value={inputs.type || ""}
            onChange={handleChange}
          />
        </label>
        <label>
          NUMBER OF PARTICIPANTS:
          <input
            className="input"
            type="text"
            name="numberOfParticipants"
            value={inputs.numberOfParticipants || ""}
            onChange={handleChange}
          />
        </label>
        <input className="button" type="submit" value="GENERATE" />
      </form>
    </div>
  );
}

export default TournamentForm;


