import React, { useState, useEffect } from "react";
import Form from "react-bootstrap/Form";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";

const TournamentForm = (props) => {
  const [inputs, setInputs] = useState({});

  const handleInputChange = (event) => {
    const target = event.target;
    const name = target.name;
    const value = target.value;
    setInputs((values) => ({ ...values, [name]: value }));
  };

  const handleCreateTournament = (event) => {
    event.preventDefault();
    const tournament = JSON.stringify({
      name: inputs.name,
      type: inputs.type,
      participants: inputs.participants,
    });
    console.log(tournament);
    create(tournament);
  };

  const create = (tournament) => {
    props.createTournament(tournament);
  };

  const handleOpenTournament = (tournamentId) => {
    props.openTournament(tournamentId);
  };

  async function handleEditTournament(tournamentId) {
    props.editTournament(tournamentId);
  }

  return (
    <Container>
      <Row className="align-items-center">
        <Col>
          <Form className="register-form">
            <Form.Group controlId="tournamentName">
              <Form.Label>TOURNAMENT NAME:</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter tournament name"
                name="tournamentName"
                onChange={handleInputChange}
              />
            </Form.Group>
            <Form.Group controlId="tournamentType">
              <Form.Label>TOURNAMENT TYPE:</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter tournament type"
                name="tournamentType"
                onChange={handleInputChange}
              />
            </Form.Group>
            <Form.Group controlId="numberOfParticipants">
              <Form.Label>NUMBER OF PARTICIPANTS:</Form.Label>
              <Form.Control
                type="text"
                placeholder="4"
                name="numberOfParticipants"
                onChange={handleInputChange}
              />
            </Form.Group>
            <input
              className="btn"
              onClick={handleCreateTournament}
              id="inputButton"
              value="CREATE TOURNAMENT"
            />
          </Form>
        </Col>
        <Col lg={1}></Col>
        <Col>
          <Form className="enter-form">
            <Form.Group controlId="existingTournamentName">
              <Form.Label>TOURNAMENT NAME:</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter tournament name"
                name="existingTournamentName"
                onChange={handleInputChange}
              />
            </Form.Group>
            <input
              className="btn"
              onClick={handleOpenTournament}
              id="inputButton"
              value="OPEN TOURNAMENT"
            />
            <input
              className="btn"
              onClick={handleOpenTournament}
              id="inputButton"
              value="EDIT TOURNAMENT"
            />
          </Form>
        </Col>
      </Row>
    </Container>
  );
};

export default TournamentForm;
