import React from "react";

class TournamentForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      type: "",
      numberOfParticipants: 0,
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    const target = event.target;
    const value = target.value;
    const name = target.name;

    this.setState({
      [name]: value,
    });
  }

  handleSubmit(event) {
    alert(
      "Form was submited: name - " + this.state.name + " type - " + this.state.type + " num of participants - " + this.state.numberOfParticipants);
    event.preventDefault();
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          Tournament Name:
          <input
            type="text"
            name="name"
            value={this.state.name}
            onChange={this.handleChange}
          />
          <label>
            Tournament Type:
            <input
              type="text"
              name="type"
              value={this.state.type}
              onChange={this.handleChange}
            />
          </label>
          Number of Paricipants:
          <input
            type="text"
            name="numberOfParticipants"
            value={this.state.numberOfParticipants}
            onChange={this.handleChange}
          />
        </label>
        <input type="submit" value="GENERATE" />
      </form>
    );
  }
}

export default TournamentForm;
