import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import { useState, useEffect, useNavigate } from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Navbar from "./components/Navbar/Navbar";
// import Tournaments from "./components/Tournaments";
// import Home from "./pages";
import EditTournament from "./components/EditTournament";
import Tournament from "./pages/tournament";
// import Tournaments from "./components/Tournaments";

const App = () => {
  const [inputs, setInputs] = useState({});
  const [tournaments, setTournaments] = useState("");

  useEffect(() => {
    const getTournaments = async () => {
      const tournamentsFromServer = await fetchTournaments();
      setTournaments(tournamentsFromServer);
    };

    getTournaments();
  }, []);

  const handleInputChange = (event) => {
    const target = event.target;
    const name = target.name;
    const value = target.value;
    setInputs((values) => ({ ...values, [name]: value }));
  };

  // Fetch Tournaments
  const fetchTournaments = async () => {
    const res = await fetch("http://localhost:5000/tournaments");
    const data = await res.json();

    return data;
  };

  // Fetch Tournament
  const fetchTournament = async (name) => {
    const res = await fetch(`http://localhost:5000/tournaments/${name}`);
    const data = await res.json();

    return data;
  };

  // Add Tournament
  const addTournament = async (tournament) => {
    const res = await fetch("http://127.0.0.1:5000/tournaments", {
      method: "POST",
      headers: {
        "Content-type": "application/json",
      },
      body: tournament,
    });

    const data = await res.json();

    setTournaments([...tournaments, data]);
  };

  // Delete Tournament
  const deleteTournament = async (id) => {
    const res = await fetch(`http://localhost:5000/tournaments/${id}`, {
      method: "DELETE",
    });
    //We should control the response status to decide if we will change the state or not.
    res.status === 200
      ? setTournaments(tournaments.filter((tournament) => tournament.id !== id))
      : alert("Error Deleting This Tournament");
  };

  const handleCreateTournament = async (event) => {
    event.preventDefault();
    const tournament = JSON.stringify({
      name: inputs.tournamentName,
      type: inputs.tournamentType,
      participants: inputs.numberOfParticipants,
    });
    await addTournament(tournament);
  };

  const handleOpenTournament = () => {
    console.log("Here I am");
    console.log(inputs.existingTournamentName);
  }

  return (
    <Router>
      <Routes>
        <Route exact path="/" element={Tournament}/>
        <Route path="/edit-tournament" element={<EditTournament />} />
      </Routes>
    </Router>
  );
};

export default App;
