import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import {useState, useEffect} from 'react';
import Home from "./pages";
import EditTournament from "./pages/editTournament";
import Tournament from "./pages/tournament";

const App = () => {
  const [tournaments, setTournaments] = useState([]);

  useEffect(() => {
    const getTournaments = async () => {
      const tournamentsFromServer = await fetchTournaments();
      setTournaments(tournamentsFromServer);
    };

    getTournaments();
  }, []);

  // Fetch Tournaments
  const fetchTournaments = async () => {
    const res = await fetch("http://localhost:5000/tournaments");
    const data = await res.json();

    return data;
  };

  // Fetch Tournament
  const fetchTournament = async (id) => {
    const res = await fetch(`http://localhost:5000/tournaments/${id}`);
    const data = await res.json();

    return data;
  };

  // Add Tournament
  const addTournament = async (tournament) => {
    const res = await fetch("http://localhost:5000/tournaments", {
      method: "POST",
      headers: {
        "Content-type": "application/json",
      },
      body: JSON.stringify(tournament),
    });

    const data = await res.json();

    setTournaments([...tournaments, data]);

    // const id = Math.floor(Math.random() * 10000) + 1
    // const newTask = { id, ...task }
    // setTasks([...tasks, newTask])
  };

  // Delete Tournament
  const deleteTournament = async (id) => {
    const res = await fetch(`http://localhost:5000/tournaments/${id}`, {
      method: "DELETE",
    });
    //We should control the response status to decide if we will change the state or not.
    res.status === 200
      ? setTournaments(tournaments.filter((tournament) => tournament.id !== id))
      : alert("Error Deleting This Tournament");
  };

  return (
    <Router>
      <Routes>
        <Route exact path="/" element={<Home />} />
        <Route path="/edit-tournament" element={<EditTournament />} />
        <Route path="/tournament" element={<Tournament />} />
      </Routes>
    </Router>
  );
};

export default App;
