import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import { useState, useEffect } from "react";
import Form from "react-bootstrap/Form";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";

const Tournaments = (props) => {
  const tournaments = props.tournamentsData;

  const setTournaments = (tournaments) => {
    props.passTournamentData(tournaments);
  };

  useEffect(() => {
    const getTournaments = async () => {
      const tournamentsFromServer = await fetchTournaments();
      setTournaments(tournamentsFromServer);
    };

    getTournaments();
  }, []);

  // Fetch Tournaments
  const fetchTournaments = async () => {
    const res = await fetch("http://localhost:5000/tournaments");
    const data = await res.json();

    return data;
  };

  // Fetch Tournament
  const fetchTournament = async (id) => {
    const res = await fetch(`http://localhost:5000/tournaments/${id}`);
    const data = await res.json();

    return data;
  };

  // Add Tournament
  const addTournament = async (tournament) => {
    const res = await fetch("http://localhost:5000/tournaments", {
      method: "POST",
      headers: {
        "Content-type": "application/json",
      },
      body: JSON.stringify(tournament),
    });

    const data = await res.json();

    setTournaments([...tournaments, data]);

    // const id = Math.floor(Math.random() * 10000) + 1
    // const newTask = { id, ...task }
    // setTasks([...tasks, newTask])
  };

  // Delete Tournament
  const deleteTournament = async (id) => {
    const res = await fetch(`http://localhost:5000/tournaments/${id}`, {
      method: "DELETE",
    });
    //We should control the response status to decide if we will change the state or not.
    res.status === 200
      ? setTournaments(tournaments.filter((tournament) => tournament.id !== id))
      : alert("Error Deleting This Tournament");
  };

  return (
    <Container>
      <Row className="align-items-center">
        <Col>
          <Form className="register-form" onSubmit={handleCreateTournament}>
            <Form.Group controlId="tournamentName">
              <Form.Label>TOURNAMENT NAME:</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter tournament name"
                name="tournamentName"
                onChange={handleInputChange}
              />
            </Form.Group>
            <Form.Group controlId="tournamentType">
              <Form.Label>TOURNAMENT TYPE:</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter tournament type"
                name="tournamentType"
                onChange={handleInputChange}
              />
            </Form.Group>
            <Form.Group controlId="numberOfParticipants">
              <Form.Label>NUMBER OF PARTICIPANTS:</Form.Label>
              <Form.Control
                type="text"
                placeholder="4"
                name="numberOfParticipants"
                onChange={handleInputChange}
              />
            </Form.Group>
            <input
              className="btn"
              id="inputButton"
              type="submit"
              value="CREATE TOURNAMENT"
            />
          </Form>
        </Col>
        <Col lg={1}></Col>
        <Col>
          <Form className="enter-form">
            <Form.Group controlId="existingTournamentName">
              <Form.Label>TOURNAMENT NAME:</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter tournament name"
                name="existingTournamentName"
                onChange={handleInputChange}
              />
            </Form.Group>
            <Link
              // onClick={handleOpenTournament}
              className="btn btn-primary"
              to={{
                pathname: "/tournament",
                testvalue: "hello",
              }}
            >
              OPEN TOURNAMENT
            </Link>
            <Link
              className="btn btn-primary"
              to={{
                pathname: "/edit-tournament/",
                inputs,
              }}
            >
              EDIT TOURNAMENT
            </Link>
          </Form>
        </Col>
      </Row>
    </Container>
  );
};

export default Tournaments;
