import React from "react";
import "./form.css";
import InputField from "./input_field.js";

class TournamentForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      type: "",
      numberOfParticipants: 0,
    };


    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    const target = event.target;
    const name = target.name;
    const value = target.value;

    this.setState({
      [name]: value,
    });
  }

  handleSubmit(event) {
    const requestOptions = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        username: this.state.name,
        email: this.state.type,
        password: this.state.numberOfParticipants,
      }),
    };
    fetch("http://127.0.0.1:5000/user", requestOptions).then((res) =>
      res.json()
    );
    event.preventDefault();
  }

  render() {
    return (
      <div class="center">
        <form onSubmit={this.handleSubmit}>
          <InputField
            fieldName="TOURNAMENT NAME:"
            field="name"
            fieldValue={this.state.name}
          />
          <InputField
            fieldName="TOURNAMENT TYPE:"
            field="type"
            fieldValue={this.state.name}
          />
          <InputField
            fieldName="NUMBER OF PARTICIPANTS"
            field="name"
            fieldValue={this.state.numberOfParticipants}
          />
          <input class="button" type="submit" value="GENERATE" />
        </form>
      </div>
    );
  }
}

export default TournamentForm;
