import React, { useState, useEffect } from "react";
import { useNavigate} from "react-router-dom";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Navbar from "./Navbar/Navbar";
import "./Form/form.css";

const TournamentForm = (props) => {
  const [inputs, setInputs] = useState({});

  const handleInputChange = (event) => {
    const target = event.target;
    const name = target.name;
    const value = target.value;
    setInputs((values) => ({ ...values, [name]: value }));
  };

  const handleCreateTournament = (event) => {
    event.preventDefault();
    const tournament = JSON.stringify({
      name: inputs.tournamentName,
      type: inputs.tournamentType,
      participants: inputs.numberOfParticipants,
    });
    create(tournament);
  };

  const create = (tournament) => {
    props.createTournament(tournament);
  };

  const handleOpenTournament = (event) => {
    event.preventDefault();
    const name = inputs.existingTournamentName;
    open(name);
  };

  const open = (tournamentName) => {
    props.openTournament(tournamentName);
  };

  return (
    <Container>
      <Navbar />
      <Row className="align-items-center">
        <Col>
          <Form className="register-form">
            <Form.Group controlId="tournamentName">
              <Form.Label>TOURNAMENT NAME:</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter tournament name"
                name="tournamentName"
                onChange={handleInputChange}
              />
            </Form.Group>
            <Form.Group controlId="tournamentType">
              <Form.Label>TOURNAMENT TYPE:</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter tournament type"
                name="tournamentType"
                onChange={handleInputChange}
              />
            </Form.Group>
            <Form.Group controlId="numberOfParticipants">
              <Form.Label>NUMBER OF PARTICIPANTS:</Form.Label>
              <Form.Control
                type="number"
                placeholder="4"
                name="numberOfParticipants"
                onChange={handleInputChange}
              />
            </Form.Group>
            <Button
              onClick={handleCreateTournament}
              className="btn"
              id="inputButton"
            >
              CREATE TOURNAMENT
            </Button>
          </Form>
        </Col>
        <Col lg={1}></Col>
        <Col>
          <Form className="enter-form">
            <Form.Group controlId="existingTournamentName">
              <Form.Label>TOURNAMENT NAME:</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter tournament name"
                name="existingTournamentName"
                onChange={handleInputChange}
              />
            </Form.Group>
            <Button
              onClick={handleOpenTournament}
              className="btn"
              id="inputButton"
            >
              OPEN TOURNAMENT
            </Button>
            <Button
              onClick={handleOpenTournament}
              className="btn"
              id="inputButton"
            >
              EDIT TOURNAMENT
            </Button>
          </Form>
        </Col>
      </Row>
    </Container>
  );
};

export default TournamentForm;
