import React, { useEffect, useState } from "react";

function Table() {
  const [teamsData, setTeamsData] = useState({});

  const fetchData = () => {
    fetch("http://127.0.0.1:5000/team")
      .then((response) => {
        return response.json();
      })
      .then((data) => setTeamsData(data));
  };

  useEffect(() => {
    const interval = setInterval(() => {
      fetchData();
      teamsData = Array.from(teamsData);
    }, 4000);

    return () => clearInterval(interval);
  }, []);

  return (
    <table>
      <tr>
        <th>Team Name</th>
        <th>Games Played</th>
        <th>Wins</th>
        <th>Draws</th>
        <th>Losses</th>
        <th>Goals For</th>
        <th>Goals Against</th>
        <th>Points</th>
      </tr>
      {Array.from(teamsData).map((val, key) => {
        return (
          <tr key={key}>
            <td>{val.team_name}</td>
            <td>{val.games_played}</td>
            <td>{val.wins}</td>
            <td>{val.draws}</td>
            <td>{val.losses}</td>
            <td>{val.goals_for}</td>
            <td>{val.goals_against}</td>
            <td>{val.points}</td>
          </tr>
        );
      })}
    </table>
  );
}

export default Table;
