import React, { useState } from "react";
import "./form.css";

function TournamentForm() {
  const [inputs, setInputs] = useState({});

  const handleChange = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setInputs((values) => ({ ...values, [name]: value }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const requestOptions = {
      method: "POST",
      // redirect: "/about",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        tournament_name: this.state.name,
        tournament_type: this.state.type,
        participants: this.state.numberOfParticipants,
      }),
    };
    fetch("http://127.0.0.1:5000/tournament", requestOptions).then((res) =>
      res.json()
    );
  };

  return (
    <div class="center">
      <form onSubmit={handleSubmit}>
        <label>
          TOURNAMENT NAME:
          <input
            class="input"
            type="text"
            name="name"
            value={inputs.name}
            onChange={handleChange}
          />
        </label>
        <label>
          TOURNAMENT TYPE:
          <input
            class="input"
            type="text"
            name="type"
            value={inputs.type}
            onChange={handleChange}
          />
        </label>
        <label>
          NUMBER OF PARTICIPANTS:
          <input
            class="input"
            type="text"
            name="numberOfParticipants"
            value={this.state.numberOfParticipants}
            onChange={this.handleChange}
          />
        </label>
        <input class="button" type="submit" value="GENERATE" />
      </form>
    </div>
  );
}

export default TournamentForm;
