import React, { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Navbar from "../Navbar/Navbar.jsx";
import "./main.css";

function TournamentForm() {
  const [inputs, setInputs] = useState({});
  const navigate = useNavigate();
  const tournamentInfo = localStorage.getItem("tournament-info");

  const handleChange = (event) => {
    const target = event.target;
    const name = target.name;
    const value = target.value;
    setInputs((values) => ({ ...values, [name]: value }));
  };

  const handleCreateTournament = (event) => {
    event.preventDefault();
    const requestOptions = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        name: inputs.name,
        type: inputs.type,
        participants: inputs.numberOfParticipants,
      }),
    };
    fetch("http://127.0.0.1:5000/tournament", requestOptions)
      .then((response) => {
        if (!response.ok) {
          throw new Error("API request was not successful!");
        } else if (response.ok) {
          navigate("/edit-tournament", {
            parameters: response.json(response),
          });
        }
        return true;
      })
      .catch((error) => {
        alert("Failed to create tournament");
        console.log("There has been a problem", error);
      });
  };

  const handleOpenTournament = (event) => {
    event.preventDefault();
    const requestOptions = {
      method: "GET",
      headers: { "Content-Type": "application/json" },
      params: { name: inputs.existingTournamentName },
    };
    fetch(
      "http://127.0.0.1:5000/tournament/" + inputs.existingTournamentName,
      requestOptions
    ).then((response) => {
      if (!response.ok) {
        alert("Tournament not found");
        throw new Error("API request was not successful!");
      } else if (response.ok) {
        navigate("/tournament", { parameters: response.json(response) });
      }
      return true;
    });
  };

  async function handleEditTournament(event) {
    event.preventDefault();
    const name = inputs.existingTournamentName;
    const result = await fetch("http://127.0.0.1:5000/tournament/" + name, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
    });
    result = await result.json();
    console.warn("result", result);
    localStorage.setItem("tournament-info", JSON.stringify(result));
    navigate(`/edit-tournament?params=${result}`);
  }

  useEffect(() => {
    if (tournamentInfo) {
      navigate("/edit-tournament");
    }
  }, []);

  // const handleEditTournament = (event) => {
  //   event.preventDefault();
  //   const requestOptions = {
  //     method: "GET",
  //     headers: { "Content-Type": "application/json" },
  //     params: { name: inputs.existingTournamentName },
  //   };
  //   fetch(
  //     "http://127.0.0.1:5000/tournament/" + inputs.existingTournamentName,
  //     requestOptions
  //   )
  //     .then((response) => {
  //       if (!response.ok) {
  //         alert("Tournament not found")
  //         throw new Error("API request was not successful!");
  //       } else if (response.ok) {
  //         navigate(`/edit-tournament$?params=${response.json(response)}`);
  //       }
  //       return true;
  //     })
  // };

  return (
    <Container>
      <Row className="align-items-center">
        <Navbar />
        <Col>
          <form id="tournament_form" onSubmit={handleCreateTournament}>
            <label>HOME TEAM:</label>
            <input
              className="input"
              id="inputField"
              type="text"
              name="homeTeam"
              value={inputs.homeTeam || ""}
              onChange={handleChange}
            />
            <label>AWAY TEAM:</label>
            <input
              className="input"
              id="inputField"
              type="text"
              name="awayTeam"
              value={inputs.awayTeam || ""}
              onChange={handleChange}
            />
            <label>RESULT:</label>
            <input
              className="input"
              id="inputField"
              type="text"
              name="result"
              value={inputs.result || ""}
              onChange={handleChange}
            />
            <input
              className="btn"
              id="inputButton"
              type="submit"
              value="REGISTER GAME"
            />
          </form>
        </Col>
        <Col lg={1}></Col>
        <Col></Col>
      </Row>
    </Container>
  );
}

export default TournamentForm;
