import React from "react";
import "./form.css";

class TournamentForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      type: "",
      numberOfParticipants: 0,
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    const target = event.target;
    const name = target.name;
    const value = target.value;

    this.setState({
      [name]: value,
    });
  }

  handleSubmit(event) {
    const requestOptions = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        username: this.state.name,
        email: this.state.type,
        password: this.state.numberOfParticipants,
      }),
    };
    fetch('http://127.0.0.1:5000/user', requestOptions)
    .then(res => res.json())
    .then(alert(data));
    event.preventDefault();
  }

  render() {
    return (
      <div class="center">
        <form onSubmit={this.handleSubmit}>
          <label>
            Tournament Name:
            <input
              type="text"
              name="name"
              value={this.state.name}
              onChange={this.handleChange}
            />
            <label>
              Tournament Type:
              <input
                type="text"
                name="type"
                value={this.state.type}
                onChange={this.handleChange}
              />
            </label>
            Number of Paricipants:
            <input
              type="text"
              name="numberOfParticipants"
              value={this.state.numberOfParticipants}
              onChange={this.handleChange}
            />
          </label>
          <input type="submit" value="GENERATE" />
        </form>
      </div>
    );
  }
}

export default TournamentForm;
