import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./form.css";

function TournamentForm() {
  const [inputs, setInputs] = useState({});
  const navigate = useNavigate();

  const handleChange = (event) => {
    const target = event.target;
    const name = target.name;
    const value = target.value;
    setInputs((values) => ({ ...values, [name]: value }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const requestOptions = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        tournament_name: inputs.name,
        tournament_type: inputs.type,
        participants: inputs.numberOfParticipants,
      }),
    };
    fetch("http://127.0.0.1:5000/tournament", requestOptions)
      .then((response) => {
        if (!response.ok) {
          throw new Error("API request was not successful!");
        } else if (response.ok) {
          navigate("/about");
        }
        return response.json(response);
      })
      .catch((error) => {
        alert("Failed to create tournament");
        console.log("There has been a problem", error);
      });
  };

  return (
    <div className="container-fluid">
        {/* <div className="row"> */}
          <form id="tournament_form" onSubmit={handleSubmit}>
            <label>TOURNAMENT NAME:</label>
            <input
              className="input"
              id="inputField"
              type="text"
              name="name"
              value={inputs.name || ""}
              onChange={handleChange}
            />
            <label>TOURNAMENT TYPE:</label>
            <input
              className="input"
              id="inputField"
              type="text"
              name="type"
              value={inputs.type || ""}
              onChange={handleChange}
            />
            <label>NUMBER OF PARTICIPANTS:</label>
            <input
              className="input"
              id="inputField"
              type="text"
              name="numberOfParticipants"
              value={inputs.numberOfParticipants || ""}
              onChange={handleChange}
            />
            <input
              className="btn btn-primary"
              id="inputButton"
              type="submit"
              value="GENERATE"
            />
          </form>
        {/* </div> */}
    </div>
  );
}

export default TournamentForm;
